import React, { Component } from 'react'
import { Header, Menu, Dropdown, Input } from 'semantic-ui-react'

export default class MenuExampleText extends Component {
  constructor(props) {
  super(props);

  this.cat = props.cat;

  this.state = {
    isShow: true,
  };
}
  state = {}

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {
    const { activeItem } = this.state

    return (
      <Menu vertical>
      <Dropdown item text='Categories'>
        <Dropdown.Menu>
          <Dropdown.Item>Politics</Dropdown.Item>
          <Dropdown.Item>Self Help</Dropdown.Item>
          <Dropdown.Item>Fiction</Dropdown.Item>
          <Dropdown.Item>Science and Technology</Dropdown.Item>
        </Dropdown.Menu>
      </Dropdown>

      {this.cat.map((item, index) => (
        <Menu.Item
          name={item.title}
          active={activeItem === 'book1'}
          onClick={this.handleItemClick}
        >
        <div class="ui top left attached label">{item.category}</div>
          <Header as='h4'>{item.title}</Header>
          <p>{item.description}</p>
        </Menu.Item>
            ))}


      </Menu>
    )

  }
}
